import React from 'react'
import { useState } from 'react';
import axios from 'axios';
import {useNavigate} from "react-router-dom";
import contextAuth from '../../context/contextAuth';

export default function Connexion() {

  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");

  const navigate = useNavigate();

  const {token,settoken}= useContext(contextAuth);

  const valueEmail = (event)=>{
    setemail(event.target.value);
  }

  const valuePassword = (event)=>{
    setpassword(event.target.value);
  }

  const handleSubmit = (event) =>{
    event.preventDefault();
    const connexion = {email,password};
    axios.post("http://localhost:5000/user/login",connexion)
    .then((resultat)=>
    {
      localStorage.setItem("token",resultat.data);

      navigate("/");
    })
    .catch((erreur)=>{console.log(erreur.response.data)});
  }
  return (
    <div>
         <form onSubmit={handleSubmit}>
            <input type="text" placeholder="email" value={email} onChange={valueEmail}/>
            <input type="text" placeholder="password" value={password} onChange={valuePassword}/>
            <button>connexion</button>
        </form>
    </div>
  )
}
