import React from 'react'
import axios from "axios";
import { useEffect } from 'react';
import { useState } from 'react';

export default function ListesAnnonces({token}) {
     
    const [annonces, setannonces] = useState([]);

    useEffect(() => {
        console.log("oui");
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.get("/annonce/getAnnonceUser",authorization)
        .then((reponse)=>{setannonces(reponse.data);})
        .catch((erreur)=>{console.log(erreur)});
       
    },[]);

    const deleteAnnonce = (id)=>{
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.delete(`/annonce/${id}`,authorization)
        .then((res)=> setannonces(annonces.filter((annonces)=>annonces._id != id)))
        .catch((err)=>console.log(err));
    }

  return (
    <div>
      
        {
            annonces.map((annonces)=>{
                return(
                <div key={annonces._id}>
                    <p>{annonces.nom}</p>
                    <p>{annonces.prix}</p>
                    <p>{annonces.description}</p>
                    <p>{annonces.photo}</p>
                    <p>{annonces.qteDispo}</p>
                    <button onClick={()=>deleteAnnonce(annonces._id)}>supprimer</button>
                    <button>modifier</button>
                </div>
                
                )  
        })}
        
    </div>
  )
}
