import React from 'react'
import { useState } from 'react';
import axios from "axios";

export default function FormAnnonces({
  token,
  annonces,
  setannonces,
  nom,
  setnom,
  prix,
  setprix,
  qte,
  setqte,
  description,
  setdescription,
  photo,
  setphoto,
  id,
  setid,
}) {
    

    const creerAnnonce = (e)=>{
        const annonce = {nom,prix,description,photo,qteDispo:qte};
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.post("/annonce",annonce,authorization)
        .then((resultat)=>{
            setannonces([resultat.data, ...annonces]);
            setnom("");
            setphoto("");
            setprix("");
            setdescription("");
            setqte("");
        })
        .catch((err)=>console.log(err));
    }

    const updateAnnonce =()=>{
        if(!id){
            return;
        }
        const annonce = {nom,prix,description,photo,qte};
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.put(`/annonce/${id}`,annonce,authorization)
        .then((res)=>console.log(res))
        .catch((err)=>console.log(err));
        
    }

  return (
    <div>
        <div >
            <input type="text" placeholder="nom" value={nom} onChange={(e)=>{setnom(e.target.value)}}/>
            <input type="text"  placeholder="prix" value={prix} onChange={(e)=>{setprix(e.target.value)}}/>
            <input type="text" placeholder="description" value={description} onChange={(e)=>{setdescription(e.target.value)}}/>
            <input type="text" placeholder="photo" value={photo} onChange={(e)=>{setphoto(e.target.value)}}/>
            <input type="text" placeholder="quantité disponible" value={qte} onChange={(e)=>{setqte(e.target.value)}}/>
            <button onClick={creerAnnonce}>créer l'annonce</button>
            <button onClick={updateAnnonce}>maj annonce</button>
        </div>
    </div>
  )
}
