import React from 'react'
import { useState } from 'react'
import axios from 'axios';
import { useContext } from 'react';
import { AuthContext } from '../../Context/AuthContext';

export default function CreerPost() {
  const [titrePost, settitrePost] = useState("");
  const [MsgPost, setMsgPost] = useState("");
  const {token,settoken}  = useContext(AuthContext);
  const [errorTitre, seterrorTitre] = useState("");
  const [errorMsg, seterrorMsg] = useState("");
  

  const validate = (e)=>{
    e.preventDefault();

    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    if(titrePost ===""){
      return seterrorTitre("le titre ne peux pas être vide.");
    }

    const post = {};
    post.titre = titrePost;
    post.description = MsgPost;
    
    axios.post("/post/creerPost",post,config)
    .then((res)=>console.log(res))
    .catch((err)=>console.log(err))

  }

  return (
    <div>
        <form>
          <p>
            <label>titre : </label>
            <input value={titrePost} onChange={(e)=>settitrePost(e.target.value)} type="text" placeholder="titre"/>
            <span>{errorTitre}</span>
          </p>
          <p>
            <label>premier message : </label>
            <input value={MsgPost} onChange={(e)=>setMsgPost(e.target.value)} type="text" placeholder="message"/>
            <span>{errorMsg}</span>
          </p>
            <button onClick={validate}>valider</button>
        </form>
    </div>
  )
}
