import React from 'react'
import { useState } from 'react'
import axios from 'axios';
import { useContext } from 'react';
import { AuthContext } from '../../Context/AuthContext';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import "../forum/accueilForum.css";

export default function AccueilForum() {
const [listePost, setlistePost] = useState([]);
const {token,settoken}  = useContext(AuthContext);
useEffect(() => {
    const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };
    axios.get("/post")
    .then((res)=>setlistePost(res.data))
    .catch((err)=>console.log(err));
}, [])


  return (
    <div>
       <h1>forum</h1>
       <div>
            <button className='nouveauSujet'>nouveau sujet</button>
            <div className='partieRecherchePost'>
                
            </div>
       </div>
       <div className='enteteAccueilAllPost'>
            <p>Sujet</p>
            <p>Catégorie</p>
            <p>Auteur</p>
            <p>Nb messages</p>
            <p>Date création</p>
            <p>Heure création</p>
       </div>
        {
            listePost.map((element)=>{
                return(
                    <div key={element._id} className='ListeAccueilAllPost'>
                        <p>{element.titre}</p>
                        <p>{element.categorie}</p>
                        <p>{element.pseudoCreateur}</p>
                        <p>{element.nombreMessages}</p>
                        <p>{element.dateCreation}</p>
                        <p>{element.heureCreation}</p>
                        
                     </div>
                )
            })
        }
    </div>
  )
}
