import React from 'react'
import { useState } from 'react'
import axios from 'axios';
import { useContext } from 'react';
import { AuthContext } from '../../Context/AuthContext';

export default function CreerPost() {
  const [titrePost, settitrePost] = useState("");
  const [MsgPost, setMsgPost] = useState("");
  const {token,settoken}  = useContext(AuthContext);
  const [errorTitre, seterrorTitre] = useState("");
  const [errorMsg, seterrorMsg] = useState("");
  const [nbCaractereMsg, setnbCaractereMsg] = useState(MsgPost.length);
  const [nbCaractereTitre, setnbCaractereTitre] = useState(titrePost.length);

  const validate = (e)=>{
    e.preventDefault();

    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    if(titrePost ==="" || MsgPost ===""){
      if(titrePost==="" && MsgPost ===""){
        seterrorTitre("Le titre ne peut pas être vide");
        seterrorMsg("Le message ne peut pas être vide");
        return ;
      }else if(titrePost === "" && MsgPost != ""){
        seterrorTitre("Le titre ne peut pas être vide");
        console.log(MsgPost.length);
        return;
      } else if(MsgPost === "" && titrePost != ""){
        seterrorMsg("Le message ne peut pas être vide");
        return;
      }
    }

    

    const post = {};
    post.titre = titrePost;
    post.description = MsgPost;
    
    axios.post("/post/creerPost",post,config).then((res)=>console.log(res)).catch((err)=>console.log(err))

  }

  const test = nbCaractereTitre.length===0? ("non"):("oui");
  return (
    <div>
        <form>
          <p>
            <label>titre : </label>
            <input value={titrePost} onChange={(e)=>{settitrePost(e.target.value);seterrorTitre("");}} type="text" placeholder="titre"/>
            <label> {nbCaractereMsg} caractère</label>
          </p>
          <p>
            <span>{errorTitre}</span>
          </p>
          <p>
            <label>premier message : </label>
            <input value={MsgPost} onChange={(e)=>{setMsgPost(e.target.value);seterrorMsg("");}} type="text" placeholder="message"/>
            <label> {nbCaractereTitre} caractère</label>
          </p>
          <p>
            <span>{errorMsg}</span>
          </p>
            <button onClick={validate}>Créér Post</button>
        </form>
    </div>
  )
}
