import React from 'react'
import "./competencePoker.css"
import { useRef } from 'react'
import { useEffect } from 'react'

export default function CompetencePoker() {

    const paraTestUn = useRef();
    const paraTestDeux = useRef();

    useEffect(() => {
      paraTestUn.current.classList.add("animationDeux");
      setTimeout(() => {
        paraTestDeux.current.classList.add("animationUne");
      }, 6000);
    }, [])
    

  return (
    <div className='containerCompetencePoker'>
        <div className='titreCompetencePoker'>
            <p>
                Compétences nécéssaires pour jouer au poker
            </p>
        </div>
        <div className='allCompetencePoker'>
            <div className='premiereComptence'>
                <div> 
                    <p ref={paraTestUn} className='test'>Concentration</p>
                    <p ref={paraTestDeux} className='testdeux'>Adaptation</p>
                </div>
            </div>
            <div className='troisComptence'>
                <p>Mathématiques</p>
                <p>Compétition</p>
                <p>Emotions</p>
            </div>
            <div className='derniereComptence'>
                <p>Motivation</p>
            </div>
        </div>
    </div>
  )
}
