import React from 'react'
import { useState } from 'react';
import axios from "axios";

export default function FormAnnonces({
  token,
  annonces,
  setannonces,
  nom,
  setnom,
  prix,
  setprix,
  qte,
  setqte,
  description,
  setdescription,
  photo,
  setphoto,
  id,
  setid,
}) {
    

    const handlesubmit = (e)=>{
        e.preventDefault(); 
        const annonce = {nom,prix,description,photo,qteDispo:qte};
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.post("/annonce",annonce,authorization)
        .then((resultat)=>{
            setannonces([resultat.data, ...annonces]);
            setnom("");
            setphoto("");
            setprix("");
            setdescription("");
            setqte("");
        })
        .catch((err)=>console.log(err));
    }
  return (
    <div>
           <form onSubmit={handlesubmit}>
            <input type="text" placeholder="nom" value={nom} onChange={(e)=>{setnom(e.target.value)}}/>
            <input type="text"  placeholder="prix" value={prix} onChange={(e)=>{setprix(e.target.value)}}/>
            <input type="text" placeholder="description" value={description} onChange={(e)=>{setdescription(e.target.value)}}/>
            <input type="text" placeholder="photo" value={photo} onChange={(e)=>{setphoto(e.target.value)}}/>
            <input type="text" placeholder="quantité disponible" value={qte} onChange={(e)=>{setqte(e.target.value)}}/>
            <button>créer l'annonce</button>
        </form>
    </div>
  )
}
