import React from 'react'
import { useEffect } from 'react'
import { useContext } from 'react';
import {AuthContext} from "../../Context/AuthContext";
import axios from "axios";
import { useState } from 'react';

export default function Admin() {

  const {token,settoken}  = useContext(AuthContext);
  const [admin, setadmin] = useState(false);
  const [nonAdmin, setnonAdmin] = useState("");
  const [pseudonyme, setpseudonyme] = useState("");

  const rechercheUser = ()=>{
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const user ={"pseudonyme": pseudonyme};

    axios.post("admin/rechercheUser",user,config)
    .then((res)=>console.log(res))
    .catch((err)=>console.log(err));
  }

 

  useEffect(() => {
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    axios.get("/admin",config)
    .then((res)=>{
      if(res.data != 'admin'){
        return setnonAdmin("vous n'avez pas l'autorisation d'acceder à cette page.")
      }
      setadmin(true);
    
    })
    .catch((err)=>setnonAdmin(err.response.data));
    
  }, [])
  
  if(admin){
    return (
      <div style={{marginTop:50}}>
          <input type="text" placeholder='rechercher un utilisateur' value={pseudonyme} onChange={(e)=>{setpseudonyme(e.target.value);console.log(pseudonyme)}} ></input>
          <button onClick={rechercheUser}>valider</button>
      </div>
    )
  }
    if(!admin){
      return(
          <div>
              {nonAdmin}
          </div>
        )
    
  }
}
