import React, { useContext } from 'react'
import { useState } from 'react';
import axios from "axios";
import contextAuth from '../../context/contextAuth';
import { useEffect } from 'react';
import FormAnnonces from './FormAnnonces';

export default function Annonces() {

    const [id, setid] = useState("");
    const [annonces, setannonces] = useState("");
    const [nom, setnom] = useState("");
    const [prix, setprix] = useState("");
    const [description, setdescription] = useState("");
    const [photo, setphoto] = useState("");
    const [qte, setqte] = useState("");

    const {token} = useContext(contextAuth);

    useEffect(() => {
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.get("/annonce/getAnnonceUser",authorization)
        .then((reponse)=>{setannonces(reponse.data)})
        .catch((erreur)=>{console.log(erreur)});
    }, []);
    

    const handlesubmit = (e)=>{
        e.preventDefault(); 
        const annonce = {nom,prix,description,photo,qteDispo:qte};
        const authorization = {
            headers:{
                Authorization :   `Bearer ${token}`,
            },
        };
        axios.post("/annonce",annonce,authorization)
        .then((resultat)=>console.log(resultat))
        .catch((err)=>console.log(err));
    }

  return (
    <div>
        <FormAnnonces token={token}/>
    
        <div>
            {
                annonces.map((annonces)=>{
                 return(
                    <div key={annonces._id}>
                        <p>{annonces.nom}</p>
                        <p>{annonces.prix}</p>
                        <p>{annonces.description}</p>
                        <p>{annonces.photo}</p>
                        <p>{annonces.qteDispo}</p>
                    </div>
                 
                 )  
            })}
        </div>
    </div>
    
  )
}
