import React from 'react'
import axios from "axios";
import { useEffect } from 'react';
import { useState } from 'react';
import { post } from '../../../../server/routes/postRoute';

export default function Post() {

  const [posts, setposts] = useState([]);

  useEffect(() => {
   
     axios.get("/post")
    .then((res)=>{
      setposts(res.data);
      console.log(posts);
    })
    .catch((err)=>console.log(err));

  }, [])
  
  if (!post.length){
    return(
      <div>
          tableau vide
      </div>
    )
  }else{
     return(
      <div>
          {posts.map((post)=>{
            return(
              <div key={post._id}>
                  <p>post.titre</p>
                  <p>post.createdAt</p>
              </div>
            )
          })}
      </div>
     )
  }
}
