import React from 'react'
import { useParams } from "react-router-dom";
import { useState } from 'react';
import { useEffect } from 'react';
import axios from "axios";
import { useContext } from 'react';
import {AuthContext} from "../../Context/AuthContext";


export default function DetailsSignalementMsg() {
  const {token}  = useContext(AuthContext);
  const {id} = useParams();
  const [signalement, setsignalement] = useState("");
  const [nbsignalement, setnbsignalement] = useState("");
  const [msgErreur, setmsgErreur] = useState("");
 
  useEffect(() => {
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    axios.get(`/signalement/afficherMsgSignaler/${id}`,config)
    .then((res)=>{
      setsignalement(res.data);
      console.log(res.data);
    })
    .catch((err)=>console.log(err));

    axios.get(`/signalement/nbFoisMsgSignaler/${id}`,config)
    .then((res)=>{
      setnbsignalement(res.data);
    })
    .catch((err)=>console.log(err));

    

  }, [])
  
  const cliqueCheckBan = ()=>{
    const bouttonBan= document.getElementById("ban");
    const bouttonSup= document.getElementById("sup");
    const bouttonRas= document.getElementById("rien");

    if(bouttonBan.checked & bouttonRas.checked){
      return setmsgErreur(`vous ne pouvez pas cocher "bannier utilisateur" et "ne rien faire."`)
    }
    if(bouttonSup.checked & bouttonRas.checked){
      return console.log("non plus");
    }
    
  
  }
 
  return (
    <div>
        <p>
          <span>Message cr√©er par {signalement.nomCreateurMessage} {signalement.prenomCreateurMessage} (pseudo : {signalement.pseudoCreateurMessage}) </span>
          <span>nombre de signalements : {nbsignalement}</span>
        </p>
        <p>contenu : {signalement.contenu} </p>
        <p>
          <label htmlFor="">bannir utilisateur </label>
          <input  id="ban" type="checkbox"/>
          <label htmlFor="">supprimer message </label>
          <input  id="sup" type="checkbox"/>
          <label htmlFor="">ne rien faire </label>
          <input  id="rien" type="checkbox"/>
        </p>
        <p>{msgErreur}</p>
        <p>
          <button onClick={cliqueCheckBan}>valider</button>
        </p>
    </div>
  )
}
