import React from 'react'
import "./competencePoker.css"
import { useRef } from 'react'
import { useEffect } from 'react'

export default function CompetencePoker() {

    const paraTestUn = useRef();
    const paraTestDeux = useRef();
    
    const cliqueEUnpremiereCompetence = ()=>{
        paraTestUn.current.disabled=true;
        paraTestDeux.current.disabled=true;
        paraTestUn.current.classList.add("rotationDeBaseDeux");
        paraTestUn.current.classList.remove("deuxiemeRotations");
        paraTestUn.current.classList.add("premiereRotations");
        setTimeout(() => {
            paraTestDeux.current.classList.add("deuxiemeRotations");
        }, 1000);
        setTimeout(() => {
            paraTestDeux.current.disabled=false;
            paraTestUn.current.classList.add("rotationDeBase");
            paraTestUn.current.classList.remove("premiereRotations");
        }, 2100);
    }

    const cliqueEDeuxpremiereCompetence = ()=>{
        paraTestDeux.current.disabled=true;
        paraTestDeux.current.classList.add("rotationDeBaseDeux");
        paraTestDeux.current.classList.remove("deuxiemeRotations");
        paraTestDeux.current.classList.add("premiereRotations");
        setTimeout(() => {
            paraTestUn.current.classList.add("deuxiemeRotations");
        }, 1000);
        setTimeout(() => {
            paraTestUn.current.disabled=false;
        }, 2100);
    }
    

  return (
    <div className='containerCompetencePoker'>
        <div className='titreCompetencePoker'>
            <p>
                Compétences nécéssaires pour jouer au poker
            </p>
        </div>
        <div className='allCompetences'>
            <div className='premiereCompetence'>
                <button onClick={cliqueEUnpremiereCompetence} ref={paraTestUn} className='eUnpremiereCompetence'>Adaptation</button>
                <button onClick={cliqueEDeuxpremiereCompetence} ref={paraTestDeux} className='eDeuxpremiereCompetence'>Patience</button>
            </div>
        </div>
    </div>
  )
}
